package com.example.exampleapiimport android.content.Contextimport com.chuckerteam.chucker.api.ChuckerInterceptorimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryclass Network {    companion object{        fun getRetrofit(context: Context) : Retrofit{            var okHttpClient = OkHttpClient                .Builder().addInterceptor(ChuckerInterceptor.Builder(context).build())                .build()             return Retrofit.Builder().baseUrl("https://api-football-standings.azharimm.dev")                 .client(okHttpClient)                 .addConverterFactory(GsonConverterFactory.create())                 .build()        }    }}